def are_arrays_close(arr1, arr2):
    return np.allclose(arr1, arr2, atol=1e-2)

def test_binary_contingency_table(has_cancer, fake_binary_pred):
    target = has_cancer
    feature = fake_binary_pred
    expected = pd.DataFrame({
        '0': [311, 32],
        '1': [46, 180]
    }, index=['0', '1'])
    actual = binary_contingency_table(target, feature)

    assert are_arrays_close(actual.values, expected.values), \
        f"""BINARY-001: Expected binary_contingency_table to return
        {expected.values}
        
        but got
        
        {actual.values}"""